%% 3D Multimodal Sensor Node Distribution Generation
% Generates 3D sensor node positions with multimodal terrain features
% Node coordinates include (x, y, z), where z coordinate is generated by terrain function

clear all;      % Clear workspace variables
clc;            % Clear command window

%% Parameter Settings
nodeCount = 100;                % Number of sensor nodes
xRange = [-50, 50];              % x-coordinate range (first quadrant)
yRange = [-50, 50];              % y-coordinate range (first quadrant)

%% Generate Node Planar Coordinates
% Generate random x,y coordinates within the specified range
nodePositions = zeros(3, nodeCount);  % Pre-allocate memory: 3xnodeCount matrix
nodePositions(1, :) = (xRange(2) - xRange(1)) * rand(1, nodeCount) + xRange(1);  % x-coordinates
nodePositions(2, :) = (yRange(2) - yRange(1)) * rand(1, nodeCount) + yRange(1);  % y-coordinates

%% Calculate Node Elevation (z-coordinate) - Multimodal Terrain
% Use sine-cosine combination function to simulate multimodal terrain
for i = 1:nodeCount
    x = nodePositions(1, i);
    y = nodePositions(2, i);
    % multimodal terrain function (sine-cosine combination)
    % Using normalized coordinates (0-100) scaled appropriately
    nodePositions(3, i) = 50 * sin(0.05 * x) * cos(0.05 * y);  
end

%% Adjusted for first quadra
New_nodePositions(1, :)=nodePositions(1, :)+50;
New_nodePositions(2, :)=nodePositions(2, :)+50;
New_nodePositions(3, :)=nodePositions(3, :)+50;

%% Result Visualization (Optional)
%Uncomment the following lines to visualize node distribution
scatter3(New_nodePositions(1,:), New_nodePositions(2,:), New_nodePositions(3,:), 'filled');
xlabel('X/(m)'); ylabel('Y/(m)'); zlabel('Z/(m)');
title('multimodal distributed sensor network');
grid on;


